import React from 'react'
import {useState, useEffect} from'react'
import UnidadPage from '../../pages/UnidadPage';

export default function Unidad() {
    const url='http://127.0.0.1:3001/unidad_medida';
    const initUnidad={
        UNI_CODIGO:'',
        UNI_MED:'',
        UNI_DETALLE:'',
        UNI_ESCALA:'',
        UNI_SIMBOLO:'',
        UNI_SIMBOLOGIA:''
    }

    const [unidad, setUnidad]= useState(initUnidad);
    const[unidades,setUnidades] = useState([]);
    const [cargado,setCargado] = useState(false);

    useEffect(()=>{},[]);
    const traerDatos=async()=>{
        let datos = await fetch(url).then(resp=>resp.json());
        console.log('traerDatos::',datos);
        setUnidades(datos);
        setCargado(datos.lenght>0);
    }
    useEffect(()=>{
        traerDatos();
    },[]);
    const cambiarValor=(e)=>{
      const{name:value}=e.target;
      setUnidad({...unidad,
      [name]:value});

    }
  return (
    <div className='row'>
      <div className='col-md-4'>
        <UnidadPage unidad={unidad} cambiarValor={cambiarValor}></UnidadPage>
      </div>
    </div>
  )
}
