import React,{useState, useEffect} from 'react'
import { Button, Form, FormGroup, Label, Input } from 'reactstrap'

export default function RolAgregar(PROPS) {
    const url='http://127.0.0.1:3001/rol';
const initRol={
    ROL_CODIGO:'',
    ROL_NOMBRE:'',
    ROL_DESCRIPCION:'',
    
}
const [rol, setRol]= useState(initRol);
const[roles,setRoles] = useState([]);
const [cargado,setCargado] = useState(false);

const traerDatos=async()=>{
  let datos = await fetch(url).then(resp=>resp.json());
  console.log('traerDatos::',datos);
  setRoles(datos);
  setCargado(!datos.length>0);
}
useEffect(()=>{
  traerDatos();
},[]);

const cambiarValor=(e)=>{
  const {name,value}=e.target;
  setRol({...rol,
  [name]:value});

 

} 
const enviarDatos =async(e)=>{
    e.preventDefault();
    if(rol['_id']===null|| rol['_id']===undefined){
        await fetch(url,{
            headers:{
                Accept:'application/json',
            'Content-type':'application/json'
            },
            method:'Post',       
            body:JSON.stringify(rol)
        }).then(resp=>traerDatos())
        .catch(error=>console.log(error))
    }else{
        await fetch(url+'/'+rol['_id'],{
            headers:{
                Accept:'application/json',
            'Content-type':'application/json'
            },
            method:'Put',       
            body:JSON.stringify(rol)
            
        }).then(resp=>traerDatos())
        
        .catch(error=>console.log(error))
    }
    setRol(initRol);

    }
    const parametrizarEditar =(dato)=>{
        setRol(dato);
    }
return (
    <Form>
    <div className="card-header">
        <h3>Roles o Eventos</h3>
    </div>
    <div className="card-body">
        
        <FormGroup>
            <Label for="codigo">Codigo de rol</Label>
            <Input 
            type="text" 
            name="codigo" 
            id="codigo" 
            placeholder="Digite el codigo del rol" />
        </FormGroup>
        <FormGroup>
            <Label for="nombre"> Nombre </Label>
            <Input 
            type="text" 
            name="nombre" 
            id="nombre" 
            placeholder="Digite el nombre"/>
        </FormGroup>
        <FormGroup>
            <Label for="descripcion">Descripcion</Label>
            <Input 
            type="text" 
            name="descripcion" 
            id="descripcion" 
            placeholder="Digite el nombre " />
        </FormGroup>
        
        
        
        <Button className='btn btn-success'>Agregar</Button>
        
        <Button className='btn btn-secondary' >Limpiar</Button>
        <Button variant="primary" href="/menuSeguridad/rol">Regresar</Button>
       
    </div></Form>
  )
}
