/* eslint-disable react/style-prop-object */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from 'react'
import Cookies from "universal-cookie/es6"
import axios from 'axios'

export default function LoginPage() {

  const URL ="http://127.0.0.1:3001/usuario";
  const cookies = new Cookies();
  const initLogin = {
    loading:false,
    USU_NOMBRE:'',
    USU_CONTRA:'',
  }
  const[login, setLogin] = useState(initLogin);

  const controlarCambio =(e)=>{
    const{name,value} = e.target;
    setLogin({
      ...login,
      [name]:value
    })
  }
 
  return (
   
    
<div class="col-auto text-center">
  
    <form >
<img class= "mt-4 mb-4" src="https://img.icons8.com/ios/344/user--v1.png" height="72" alt="User icon"/>
<h1 class = "h3 mb-4 font-weight-normal"> Ingresar </h1>
<label for= "Name" class ="sr-only"> </label>
<input type ="user" id="Name"
class="form-control" placeholder = "Name" required autofocus
onChange={controlarCambio}/> 
<label for= "password"
class ="sr-only"></label>
<input type="password" id="password"
placeholder="Password" class="form-control" required autofocus
onChange={controlarCambio}/>
<div class = "mt-3">
 <button class = "btn btn-success btn-primary btn-block " > Aceptar </button>
 <button class = "btn btn-danger btn-primary btn-block"> Borrar</button>


</div>
</form>
</div>
     
  )
}
