import React,{useState, useEffect} from 'react'

import { Button, Form, FormGroup, Label, Input } from 'reactstrap'
import { FormCheck } from 'react-bootstrap'
export default function ConsecutivoAgregar() {

    const url='http://127.0.0.1:3001/rol';
const initConsecutivo={
    CONS_TIPO:'',
    CONS_DESCRIPCION:'',
    CONS_VALOR:'',
    CONS_PREFIJO:'',
    
}
const [consecutivo, setConsecutivo]= useState(initConsecutivo);
const[consecutivos,setConsecutivos] = useState([]);
const [cargado,setCargado] = useState(false);

const traerDatos=async()=>{
  let datos = await fetch(url).then(resp=>resp.json());
  console.log('traerDatos::',datos);
  setConsecutivos(datos);
  setCargado(!datos.length>0);
}
useEffect(()=>{
  traerDatos();
},[]);

const cambiarValor=(e)=>{
  const {name,value}=e.target;
  setConsecutivo({...consecutivo,
  [name]:value});

 

}
  return (
    <Form>
    <div className="card-header">
        <h3>Consecutivos</h3>
    </div>
    <div className="card-body">
        
        <FormGroup>
            <Label for="codigo">Tipo de Consecutivo</Label>
            <Input 
            type="text" 
            name="codigo" 
            id="codigo" 
            placeholder="Digite el codigo del usaurio" />
        </FormGroup>
        <FormGroup>
            <Label for="descripcion">Descripcion</Label>
            <Input 
            type="text" 
            name="descripcion" 
            id="descripcion" 
            placeholder="Digite el nombre " />
        </FormGroup>
        <FormGroup>
            <Label for="valor"> Valor Consecutivo </Label>
            <Input 
            type="text" 
            name="valor" 
            id="valor" 
            placeholder="Digite el valor"/>
        </FormGroup>
        <FormCheck 
    type="switch"
    id="PrefijoCheck"
    label="El consecutivo posee Prefijo"
  />
        <FormGroup>
            <Label for="prefijo">Prefijo</Label>
            <Input 
            type="text" 
            name="prefijo" 
            id="prefijo" 
            placeholder="Digite el prefijo " />
        </FormGroup>
        
        <Button className='btn btn-success'>Agregar</Button>
        
        <Button className='btn btn-secondary' >Limpiar</Button>
        <Button variant="primary" href="/menuSeguridad/consecutivo">Regresar</Button>
       
    </div></Form>
  )
}
